import Detail from "./detail/DetailContent.vue";
import { ref } from "vue";
export default {
  name: "detail-page",

  data() {
    return {
      ani: {}
    };
  },

  components: {
    Detail
  },

  created() {
    // var variables = {
    //   id: this.$route.params.animeId,
    // };
    console.log(this.$route.params.animeId);
    var query = `query {
  Media(type: ANIME, id: 15125) {
    id
    title {
      romaji
    }
    status
    description
    coverImage {
      extraLarge
    }
    season
    seasonYear
    meanScore
    genres
    popularity
    format
    season
    status
    seasonYear
    episodes
    duration
  }
}
`; // Define the config we'll need for our Api request

    var url = "https://graphql.anilist.co",
        options = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json"
      },
      body: JSON.stringify({
        query: query
      })
    };
    const anime = ref();
    fetch(url, options).then(response => response.json()).then(data => {
      anime.value = data.data;
      this.ani = data.data;
      console.log(anime);
    });
    return anime;
  }

};
