import { createRouter, createWebHistory } from "vue-router";

import Home from "../components/HomePage.vue";
import Search from "../components/SearchPage.vue";
import Detail from "../components/DetailPage.vue";
import DetailOverview from "../components/detail/DetailOverview.vue";

const routes = [
    { path: "/", redirect: "/home" },
    { path: "/home", name: "home-page", component: Home },
    { path: "/search", name: "search-page", component: Search },
    {
        path: "/anime/:animeId",
        name: "anime-page",
        component: Detail,
        children: [{
            path: "ove",
            name: "overview-page",
            component: DetailOverview
        }, ],
    },
];

const router = new createRouter({
    history: createWebHistory(),
    routes,
});

export default router;
