import Detail from "./detail/DetailContent.vue";
import { ref } from "vue";
export default {
  name: "detail-page",

  data() {
    return {};
  },

  components: {
    Detail
  },

  startup() {
    var variables = {
      id: this.$route.params.animeId[0]
    };
    console.log(this.$route.params.animeId[0]);
    var query = `query {
  Media(type: ANIME, id: $id) {
    id
    title {
      romaji
    }
    status
    description
    coverImage {
      extraLarge
    }
    season
    seasonYear
    meanScore
    genres
    popularity
    format
    season
    status
    seasonYear
    episodes
    duration
  }
}
`; // Define the config we'll need for our Api request

    var url = "https://graphql.anilist.co",
        options = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json"
      },
      body: JSON.stringify({
        query: query,
        variables: variables
      })
    };
    const anime = ref();
    fetch(url, options).then(response => response.json()).then(data => {
      anime.value = data.data;
    });
    return anime;
  }

};
